(function($)
{
    $("#contactame-back").click(function(){
         $(".contactame .backbg").hide();
         $(".contactame form").hide();
         $("#contactame-back").hide();
         $(".contactame .contact-border-yellow").show();
         $(".contactame .contact-subtitle").show();
         $("#contactame-more").show();
    });
    
    $("#contactame-more").click(function(){
        
         $(".contactame .backbg").show();
         $(".contactame form").show();
         $("#contactame-back").show();
         $(".contactame .contact-border-yellow").hide();
         $(".contactame .contact-subtitle").hide();
         $("#contactame-more").hide();
         
    });
    
    $("#btnBuy").click(function(){
        $(".sidebar" ).addClass( "move" );
    });

    $("#close-sidebar").click(function(){
        $(".sidebar" ).removeClass( "move" );
    });
})( jQuery );